INSERT INTO `wp_posts` VALUES (211,1,'2024-07-29 15:06:44','2024-07-29 15:06:44','/* Styles généraux pour le formulaire Contact Form 7 */\n.wpcf7-form {\n    max-width: 600px;\n    margin: 0 auto;\n    padding: 20px;\n    background: #f9f9f9;\n    border: 1px solid #ddd;\n    border-radius: 10px;\n}\n\n/* Conteneur Flexbox pour les produits */\n.products {\n    display: flex;\n    flex-wrap: wrap; /* Permet de passer à la ligne suivante si nécessaire */\n    justify-content: space-between; /* Espace entre les éléments */\n    gap: 10px; /* Ajoute un espacement entre les éléments */\n    margin-bottom: 20px;\n}\n\n.product {\n    flex: 1 1 calc(50% - 10px); /* Chaque champ prend 48% de la largeur moins l\'espacement */\n    box-sizing: border-box; /* Assure que padding et border sont inclus dans la largeur */\n}\n\n/* Conteneur Flexbox pour les informations personnelles */\n.user-info {\n    display: flex;\n    flex-direction: column; /* Alignement en colonne */\n    margin-bottom: 20px;\n}\n\n.user-field {\n    margin-bottom: 10px;\n    padding: 10px;\n    border: 1px solid #ccc;\n    border-radius: 5px;\n}\n\n/* Conteneur Flexbox pour les informations de livraison */\n.delivery-info {\n    display: flex;\n    flex-direction: column; /* Alignement en colonne */\n    margin-bottom: 20px;\n}\n\n.delivery-field {\n    margin-bottom: 10px;\n    padding: 10px;\n    border: 1px solid #ccc;\n    border-radius: 5px;\n}\n\n/* Bouton d\'envoi centré */\n.submit-button {\n    display: flex;\n    justify-content: center;\n}\n\n.wpcf7 input[type=\"submit\"] {\n    padding: 10px 20px;\n    background-color: #0073e6;\n    color: #ffffff;\n    border: none;\n    cursor: pointer;\n    font-size: 16px;\n    text-align: center;\n    text-transform: capitalize;\n    border-radius: 5px;\n}\n\n.wpcf7 input[type=\"submit\"]:hover {\n    background-color: #005bb5;\n}\n','planty','','inherit','closed','closed','','177-revision-v1','','','2024-07-29 15:06:44','2024-07-29 15:06:44','',177,'http://planty.local/?p=211',0,'revision','',0);
